import React from 'react';
import { useNavigate } from 'react-router-dom';
import CategoryDropdown from './CategoryDropdown';
import './Header.css';

const Header = ({ 
  showCategoryDropdown = false, 
  categories = [], 
  selectedCategory = 'Ï†ÑÏ≤¥', 
  onCategoryChange = null,
  showAddButton = true,
  showBackButton = false,
  backButtonText = '‚Üê Î™©Î°ùÏúºÎ°ú'
}) => {
  const navigate = useNavigate();

  const handleAddBook = () => {
    // TODO: ÌåÄÏõêÏù¥ ÎèÑÏÑú Îì±Î°ù ÌéòÏù¥ÏßÄ ÏôÑÏÑ± ÌõÑ Ï£ºÏÑù Ìï¥Ï†ú
    // navigate('/book/new');
    
    // ÏûÑÏãú Ï≤òÎ¶¨: ÌéòÏù¥ÏßÄÍ∞Ä Ï§ÄÎπÑÎêòÏßÄ ÏïäÏïòÎã§Îäî ÏïåÎ¶º
    alert('ÎèÑÏÑú Îì±Î°ù ÌéòÏù¥ÏßÄÎäî ÌòÑÏû¨ Í∞úÎ∞ú Ï§ëÏûÖÎãàÎã§.\nÌåÄÏõêÏù¥ ÏôÑÏÑ± ÌõÑ Ïù¥Ïö© Í∞ÄÎä•Ìï©ÎãàÎã§.');
  };

  const handleHomeClick = () => {
    navigate('/');
  };

  const handleBack = () => {
    navigate('/');
  };

  return (
    <header className="header">
      <div className="header-content">
        <div className="header-left">
          {showBackButton && (
            <button className="header-back-button" onClick={handleBack}>
              {backButtonText}
            </button>
          )}
          <h1 className="header-title" onClick={handleHomeClick} style={{ cursor: 'pointer' }}>
            <span className="book-icon">üìñ</span>
            ÎèÑÏÑú Í¥ÄÎ¶¨ ÏãúÏä§ÌÖú
          </h1>
        </div>
        <div className="header-actions">
          {showCategoryDropdown && (
            <CategoryDropdown
              categories={categories}
              selectedCategory={selectedCategory}
              onCategoryChange={onCategoryChange}
            />
          )}
          {showAddButton && (
            <button className="add-button" onClick={handleAddBook}>
              +
            </button>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
